import React from 'react';
import { Modal, View, Text, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import { X, CheckCircle2, Puzzle, Rocket, Settings, Gamepad2, Coins, Share2, BarChart2 } from 'lucide-react-native';
import ScoreButton from './ScoreButton';

const SectionTitle = ({ icon, title }) => (
  <View style={styles.sectionTitleContainer}>
    {icon}
    <Text style={styles.sectionTitle}>{title}</Text>
  </View>
);

const ListItem = ({ children, checked = false }) => (
  <View style={styles.listItem}>
    <Text style={styles.listItemBullet}>{checked ? '✅' : '🔜'}</Text>
    <Text style={styles.listItemText}>{children}</Text>
  </View>
);

const RoadmapModal = ({ isVisible, onClose }) => {
  return (
    <Modal
      animationType="slide"
      transparent={false}
      visible={isVisible}
      onRequestClose={onClose}
    >
      <View style={styles.modalContainer}>
        <View style={styles.header}>
          <Text style={styles.title}>📍 Roadmap Evolutivo</Text>
          <TouchableOpacity onPress={onClose} style={styles.closeButton}>
            <X size={30} color="#a3a3a3" />
          </TouchableOpacity>
        </View>
        <ScrollView contentContainerStyle={styles.scrollContent}>
          <SectionTitle icon={<CheckCircle2 size={24} color="#84cc16" />} title="Fase 1 – MVP Actual (Completado)" />
          <ListItem checked>Lógica de Escoba de 15, Truco y Chinchón 100% funcional.</ListItem>
          <ListItem checked>App responsive con React Native + Expo.</ListItem>
          <ListItem checked>Multijugador local, sin registro.</ListItem>
          <ListItem checked>Soporte para sumar, restar puntos y deshacer jugadas.</ListItem>
          <ListItem checked>Indicadores visuales del estado del jugador (líder, último).</ListItem>
          <ListItem checked>Guardado automático al cerrar app (parcialmente, con AsyncStorage).</ListItem>
          
          <SectionTitle icon={<Puzzle size={24} color="#fb923c" />} title="Fase 2 – Mejora visual y de experiencia" />
          <ListItem checked>UI uniforme, minimalista y moderna.</ListItem>
          <ListItem>Animaciones suaves y sonidos sutiles.</ListItem>
          <ListItem>Vibración en acciones importantes (Haptics).</ListItem>
          <ListItem>Pantalla final de juego con diseño celebratorio.</ListItem>
          
          <SectionTitle icon={<Gamepad2 size={24} color="#38bdf8" />} title="Fase 3 – Nuevos juegos de cartas" />
          <ListItem>Generala, Chancho, 7 y medio, Burako y más.</ListItem>
          <ListItem>Selector para "Agregar nuevo juego" con íconos e info.</ListItem>

          <SectionTitle icon={<Settings size={24} color="#a78bfa" />} title="Fase 4 – Mejoras funcionales clave" />
          <ListItem>Personalización de jugadores (colores, íconos).</ListItem>
          <ListItem>Historial de partidas detallado.</ListItem>
          <ListItem>Estadísticas: quién gana más, promedios, etc.</ListItem>
          
          <SectionTitle icon={<Share2 size={24} color="#f472b6" />} title="Fase 5 – Social & Multijugador" />
          <ListItem>Compartir marcador en vivo por QR o enlace.</ListItem>
          <ListItem>Guardado de partidas en la nube e invitación por link.</ListItem>
          
          <SectionTitle icon={<Coins size={24} color="#fbbf24" />} title="Fase 6 – Monetización (Opcional)" />
          <ListItem>Plan PRO para desbloquear juegos y estadísticas avanzadas.</ListItem>
          <ListItem>Temas visuales personalizados (retro, neón, etc.).</ListItem>
          <ListItem checked>Botón "Invitame una cerveza".</ListItem>

        </ScrollView>
      </View>
    </Modal>
  );
};

const styles = StyleSheet.create({
    modalContainer: {
        flex: 1,
        backgroundColor: '#171717',
        paddingTop: 50,
    },
    header: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        paddingHorizontal: 20,
        paddingBottom: 20,
        borderBottomWidth: 1,
        borderBottomColor: '#262626',
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        color: '#FFFFFF',
    },
    closeButton: {
        padding: 5,
    },
    scrollContent: {
        padding: 20,
    },
    sectionTitleContainer: {
        flexDirection: 'row',
        alignItems: 'center',
        marginTop: 25,
        marginBottom: 15,
    },
    sectionTitle: {
        fontSize: 20,
        fontWeight: 'bold',
        color: '#FFFFFF',
        marginLeft: 10,
    },
    listItem: {
        flexDirection: 'row',
        alignItems: 'flex-start',
        marginBottom: 12,
        paddingLeft: 10,
    },
    listItemBullet: {
        fontSize: 16,
        marginRight: 12,
        color: '#FFFFFF',
        lineHeight: 24,
    },
    listItemText: {
        fontSize: 16,
        color: '#d4d4d4',
        flex: 1,
        lineHeight: 24,
    },
});

export default RoadmapModal;
