import React, { useEffect } from 'react';
import { View, Text, TouchableOpacity, StatusBar, StyleSheet } from 'react-native';
import { useGameStore } from '../store/gameStore';
import { Heart, Users, Trophy, ChevronRight } from 'lucide-react-native';

const GameCard = ({ icon, title, subtitle, onPress, color }) => (
  <TouchableOpacity
    onPress={onPress}
    style={styles.gameCard}
    activeOpacity={0.8}
  >
    <View style={styles.cardContent}>
      {icon}
      <View style={styles.cardText}>
        <Text style={styles.cardTitle}>{title}</Text>
        <Text style={styles.cardSubtitle}>{subtitle}</Text>
      </View>
    </View>
    <ChevronRight size={24} color="#52525b" />
  </TouchableOpacity>
);

export default function HomeScreen({ navigation }) {
  const { loadFromStorage } = useGameStore();

  useEffect(() => {
    loadFromStorage();
  }, []);

  const handleGameSelect = (game) => {
    navigation.navigate(game);
  };

  return (
    <View style={styles.container}>
      <StatusBar barStyle="light-content" />
      <View style={styles.content}>
        <View style={styles.header}>
          <Text style={styles.title}>
            Anotador
          </Text>
          <Text style={styles.subtitle}>
            Selecciona un juego
          </Text>
        </View>

        <View style={styles.gamesContainer}>
          <GameCard
            title="Truco"
            subtitle="Para 2 o 4 jugadores"
            icon={<Heart size={32} color="#84cc16" />}
            onPress={() => handleGameSelect('Truco')}
          />
          <GameCard
            title="Chinchón"
            subtitle="De 2 a 6 jugadores"
            icon={<Users size={32} color="#84cc16" />}
            onPress={() => handleGameSelect('Chinchon')}
          />
          <GameCard
            title="Escoba de 15"
            subtitle="Para 2 o 4 jugadores"
            icon={<Trophy size={32} color="#84cc16" />}
            onPress={() => handleGameSelect('Escoba')}
          />
        </View>
      </View>
      <View style={styles.footer}>
        <Text style={styles.footerText}>Hecho con ❤️ para los pibes</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#171717',
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    padding: 24,
  },
  header: {
    alignItems: 'center',
    marginBottom: 32,
  },
  title: {
    color: '#ffffff',
    fontSize: 48,
    fontWeight: 'bold',
    letterSpacing: -1,
  },
  subtitle: {
    color: '#a3a3a3',
    fontSize: 20,
    marginTop: 8,
  },
  gamesContainer: {
    gap: 20,
  },
  gameCard: {
    backgroundColor: '#262626',
    borderWidth: 1,
    borderColor: '#404040',
    borderRadius: 16,
    padding: 24,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
  },
  cardContent: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  cardText: {
    marginLeft: 20,
  },
  cardTitle: {
    color: '#ffffff',
    fontSize: 20,
    fontWeight: 'bold',
  },
  cardSubtitle: {
    color: '#a3a3a3',
    fontSize: 16,
  },
  footer: {
    alignItems: 'center',
    paddingBottom: 40,
  },
  footerText: {
    color: '#52525b',
    fontSize: 14,
  },
});
