import React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { SafeAreaProvider } from 'react-native-safe-area-context';

// Importar pantallas
import HomeScreen from './screens/HomeScreen';
import EscobaScreen from './screens/EscobaScreen';
import TrucoScreen from './screens/TrucoScreen';
import ChinchonScreen from './screens/ChinchonScreen';

const Stack = createStackNavigator();

export default function App() {
  return (
    <SafeAreaProvider>
      <NavigationContainer>
        <Stack.Navigator
          initialRouteName="Home"
          screenOptions={{
            headerStyle: {
              backgroundColor: '#1a1a1a',
            },
            headerTintColor: '#fff',
            headerTitleStyle: {
              fontWeight: 'bold',
            },
            cardStyle: { backgroundColor: '#f5f5f5' },
          }}
        >
          <Stack.Screen
            name="Home"
            component={HomeScreen}
            options={{ title: 'Cartas Counter' }}
          />
          <Stack.Screen
            name="Escoba"
            component={EscobaScreen}
            options={{ title: 'Escoba de 15' }}
          />
          <Stack.Screen
            name="Truco"
            component={TrucoScreen}
            options={{ title: 'Truco' }}
          />
          <Stack.Screen
            name="Chinchon"
            component={ChinchonScreen}
            options={{ title: 'Chinchón' }}
          />
        </Stack.Navigator>
      </NavigationContainer>
    </SafeAreaProvider>
  );
}
